record.field:
# Auto-set description based on value
for record in records:
    if not record.description:
        if record.value > 100:
            record.write({'description': f"High value item: {record.name}"})
        elif record.value > 50:
            record.write({'description': f"Medium value item: {record.name}"})
        else:
            record.write({'description': f"Low value item: {record.name}"})

# Mark records with high value in description
for record in records:
    if record.value > 100:
        desc = record.description or ''
        if '[HIGH VALUE]' not in desc:
            record.write({'description': f"[HIGH VALUE] {desc}".strip()})
